public function empfeedbackreport(Request $request)
{
    // Get the filter parameters for date range and status
    $startDate = $request->input('start_date');
    $endDate = $request->input('end_date');
    $status = $request->input('status');

    // Start building the query with a base SELECT statement
            $query = '
            SELECT
                emp_feedback_sent_log.employee_code,
                emp_feedback_sent_log.punch_date,
                emp_feedback_sent_log.feed_back_type,
                emp_feedback_sent_log.comments,
                emp_feedback_sent_log.updated_at,
                IFNULL(emp_feedback_sent_log.sent_at, "Not Available") AS sent_at,
                Master_Zimyo_Emp.EMPLOYEE_CODE AS emp_code,
                Master_Zimyo_Emp.EMPLOYEE_NAME,
                Master_Zimyo_Emp.DESIGNATION,
                Master_Zimyo_Emp.LOCATION_NAME,
                Master_Zimyo_Emp.DEPARTMENT_NAME
            FROM
                emp_feedback_sent_log
            JOIN
                Master_Zimyo_Emp
                ON emp_feedback_sent_log.employee_code = Master_Zimyo_Emp.EMPLOYEE_CODE COLLATE utf8mb4_unicode_ci
        ';

    // Initialize the parameters array
    $params = [];

    // Add date range filter if provided
    if ($startDate && $endDate) {
        $query .= ' WHERE emp_feedback_sent_log.punch_date BETWEEN :start_date AND :end_date';
        $params['start_date'] = $startDate;
        $params['end_date'] = $endDate;
    }

    // Add status filter if provided
    if ($status) {
        // Add the condition based on the status filter (Feedback Sent or Received)
        $query .= $startDate && $endDate ? ' AND ' : ' WHERE ';
        if ($status == 'sent') {
            $query .= 'emp_feedback_sent_log.feed_back_type IS NULL';
        } else {
            $query .= 'emp_feedback_sent_log.feed_back_type IS NOT NULL';
        }
    }

    // Execute the query with the parameters array
    $feedbackData = DB::select($query, $params);

    // Process the feedback data: Determine if feedback was sent or received
    foreach ($feedbackData as $feedback) {
        // If feedback type or comments are NULL, feedback is "sent"
        if (is_null($feedback->feed_back_type)) {
            $feedback->status = 'Feedback Sent'; // Mark as feedback sent
        } else {
            // If both feed_back_type and comments are not NULL, it's "feedback received"
            $feedback->status = 'Feedback Received'; // Mark as feedback received
            // Convert numeric feedback types to human-readable labels
            switch ($feedback->feed_back_type) {
                case 1:
                    $feedback->feed_back_type = 'Good ðŸ˜Š';
                    break;
                case 2:
                    $feedback->feed_back_type = "It's OK ðŸ˜";
                    break;
                case 3:
                    $feedback->feed_back_type = 'Bad ðŸ˜ž';
                    break;
                default:
                    $feedback->feed_back_type = 'Unknown'; // In case of an unknown value
            }
        }
    }

    // Pass the data to the view
    return view('reports.empfeedbackreport', ['feedbackData' => $feedbackData]);
}









