public function filterResults(Request $request)
{
    $request->validate([
        'start_date' => 'required|date_format:d-m-Y',
        'end_date' => 'required|date_format:d-m-Y|after_or_equal:start_date',
        'service' => 'nullable|exists:master_services,id',
    ]);

    $startDate = Carbon::createFromFormat('d-m-Y', $request->start_date)->startOfDay();
    $endDate = Carbon::createFromFormat('d-m-Y', $request->end_date)->endOfDay();
    $serviceId = $request->service;
    $currentBranchId = Helper::currentBranchAccess();
    // $perPage = $request->input('per_page', 10);

    $queries = [];

    $tables = [
        'patient_activity_procedure_service' => [
            'rateColumn' => 'procedure_service_amount',
            'serviceIdColumn' => 'master_service_id'
        ],
        'patient_activity_medical_emergency_care' => [
            'rateColumn' => 'emergency_care_amount',
            'serviceIdColumn' => 'master_service_id'
        ],
        // 'patient_activity_staff_extra_service' => [
        //     'rateColumn' => 'extra_service_amount',
        //     'serviceIdColumn' => 'master_service_id'
        // ],
        'patient_activity_blood_investigation' => [
            'rateColumn' => 'blood_investigation_amount',
            'serviceIdColumn' => 'master_service_id'
        ],
        'patient_activity_medical_euipments' => [
            'rateColumn' => 'medical_equipment_amount',
            'serviceIdColumn' => 'master_service_id'
        ],
    ];

    $queries = [];

    foreach ($tables as $table => $columns) {
        $rateColumn = $columns['rateColumn'];
        $serviceIdColumn = $columns['serviceIdColumn'];

        $queries[] = DB::table('patients')
            ->leftJoin($table, 'patients.id', '=', "$table.patient_id")
            ->leftJoin('master_services', 'master_services.id', '=', "$table.$serviceIdColumn")
            // Filter discharge records based on discharge date between the start and end date
            ->leftJoin(DB::raw("(SELECT patient_id, MAX(discharge_date) AS discharge_date FROM patient_discharge WHERE discharge_date BETWEEN '$startDate' AND '$endDate' GROUP BY patient_id) as pd"), 'patients.id', '=', 'pd.patient_id')
            ->leftJoin('patient_schedules', function($join) use ($table): void {
                $join->on('patients.id', '=', 'patient_schedules.patient_id')
                     ->on("$table.schedule_date", '=', 'patient_schedules.schedule_date');
            })
            ->leftJoin('master_beds', 'patient_schedules.bed_id', '=', 'master_beds.id')
            ->leftJoin('master_rooms', 'master_beds.room_id', '=', 'master_rooms.id')
            ->leftJoin(DB::raw("(SELECT patient_id, id as latest_lead_id, care_plan, status FROM leads WHERE id IN (
                SELECT MAX(id) FROM leads GROUP BY patient_id
            )) as latest_lead"), function ($join): void {
            $join->on('patients.id', '=', 'latest_lead.patient_id');
        })
            ->where('patients.branch_id', $currentBranchId)
            ->whereNull("$table.deleted_at")
           // ->where("$table.um_ack", 1)
            ->whereNotNull("$table.flows_user_id")
            ->whereBetween("$table.schedule_date", [$startDate, $endDate])
            ->whereNotNull("$table.$serviceIdColumn")
            ->when($serviceId, function($query) use ($serviceId, $table, $serviceIdColumn): void {
                $query->where("$table.$serviceIdColumn", $serviceId);
            })
            ->select(
                DB::raw('CONCAT_WS(" ", patients.name_prefix, patients.first_name, patients.middle_name, patients.last_name) as full_name'),
                'master_services.service_name',
                DB::raw("GROUP_CONCAT($table.$rateColumn ORDER BY $table.schedule_date SEPARATOR ',') as Rates"),
                DB::raw("GROUP_CONCAT(DATE_FORMAT($table.schedule_date, '%d-%m-%Y') ORDER BY $table.schedule_date SEPARATOR ',') as Schedule_Dates"),
                'patients.patient_id as patient_id',
                DB::raw('IF(pd.patient_id IS NOT NULL, "Discharged", "Ongoing") as status'), // Set status based on discharge record
                'master_rooms.room_number',
                DB::raw("'' as Service_IDs"),
                DB::raw("
        CASE
                           WHEN latest_lead.care_plan = 1 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 2 THEN 'Palliative Care'
                            WHEN latest_lead.care_plan = 3 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 4 THEN 'Transition Care'
                            WHEN latest_lead.care_plan = 5 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 6 THEN 'Rehab Care'
                            WHEN latest_lead.care_plan = 7 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 8 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 9 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 10 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 11 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 12 THEN 'Athulya Assisted Living'
                            ELSE 'Unknown'
        END AS care_plan
    ")
            )
            ->groupBy('patients.id', 'master_services.service_name', 'patients.patient_id', 'pd.patient_id', 'master_rooms.room_number');
    }

                $latestIdsSubquery = DB::table('patient_activity_staff_extra_service as pa')
                ->select(DB::raw('MAX(pa.id) as latest_id'))
                ->whereBetween('pa.schedule_date', [$startDate, $endDate])
                ->groupBy('pa.patient_id', 'pa.extra_service_id', 'pa.schedule_date');

                $queries[] = DB::table('patients')
                    ->leftJoin('patient_activity_staff_extra_service as pa', 'patients.id', '=', 'pa.patient_id')
                    ->joinSub($latestIdsSubquery, 'latest_pa', function ($join): void {
                        $join->on('pa.id', '=', 'latest_pa.latest_id');
                    })
                    ->leftJoin('master_services', 'master_services.id', '=', 'pa.extra_service_id')
                    // Filter discharge records based on discharge date between the start and end date
                    ->leftJoin(DB::raw("(SELECT patient_id, MAX(discharge_date) AS discharge_date FROM patient_discharge WHERE discharge_date BETWEEN '$startDate' AND '$endDate' GROUP BY patient_id) as pd"), 'patients.id', '=', 'pd.patient_id')
                    ->leftJoin('patient_schedules', function ($join): void {
                        $join->on('patients.id', '=', 'patient_schedules.patient_id')
                            ->on('pa.schedule_date', '=', 'patient_schedules.schedule_date');
                    })
                    ->leftJoin('master_beds', 'patient_schedules.bed_id', '=', 'master_beds.id')
                    ->leftJoin('master_rooms', 'master_beds.room_id', '=', 'master_rooms.id')
                    ->leftJoin(DB::raw("(SELECT patient_id, id as latest_lead_id, care_plan, status FROM leads WHERE id IN (
                        SELECT MAX(id) FROM leads GROUP BY patient_id
                    )) as latest_lead"), function ($join): void {
                    $join->on('patients.id', '=', 'latest_lead.patient_id');
                })

                    ->where('patients.branch_id', $currentBranchId)
                    ->whereNull('pa.deleted_at')
                  //  ->where('pa.um_ack', 1)
                    ->whereNotNull('pa.flows_user_id')
                    ->whereBetween('pa.schedule_date', [$startDate, $endDate])
                    ->when($serviceId, function($query) use ($serviceId): void {
                        $query->where('pa.extra_service_id', $serviceId);
                    })
                    ->select(
                        DB::raw('CONCAT_WS(" ", patients.name_prefix, patients.first_name, patients.middle_name, patients.last_name) as full_name'),
                        'master_services.service_name',
                        DB::raw("GROUP_CONCAT(pa.extra_service_amount ORDER BY pa.schedule_date SEPARATOR ',') as Rates"),
                        DB::raw("GROUP_CONCAT(DATE_FORMAT(pa.schedule_date, '%d-%m-%Y') ORDER BY pa.schedule_date SEPARATOR ',') as Schedule_Dates"),
                        'patients.patient_id',
                        DB::raw('IF(pd.patient_id IS NOT NULL, "Discharged", "Ongoing") as status'), // Set status based on discharge record
                        'master_rooms.room_number',
                        DB::raw("'' as Service_IDs") , // Placeholder for service IDs, ensuring consistency
                        // Adding care_plan with CASE logic
                        DB::raw("
                        CASE
                           WHEN latest_lead.care_plan = 1 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 2 THEN 'Palliative Care'
                            WHEN latest_lead.care_plan = 3 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 4 THEN 'Transition Care'
                            WHEN latest_lead.care_plan = 5 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 6 THEN 'Rehab Care'
                            WHEN latest_lead.care_plan = 7 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 8 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 9 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 10 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 11 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 12 THEN 'Athulya Assisted Living'
                            ELSE 'Unknown'
                        END AS care_plan
                    ")

                    )
                    ->groupBy('patients.id', 'master_services.service_name', 'patients.patient_id', 'pd.patient_id', 'master_rooms.room_number', 'pa.extra_service_id');


    if($serviceId==89){
        // Query for master_ratecard table (Monthly Service Charges)
    $queries[] = DB::table('master_ratecard')
    ->join('patients', 'master_ratecard.patient_id', '=', 'patients.id')

    // Subquery to get the latest master_ratecard record for service_id = 89
    ->join(DB::raw("(SELECT patient_id, MAX(id) as latest_ratecard_id FROM master_ratecard
                    WHERE service_id = 89
                    GROUP BY patient_id) as latest_rc"), 'master_ratecard.id', '=', 'latest_rc.latest_ratecard_id')

    // Subquery to get the latest discharge date from patient_discharge table
    ->leftJoin(DB::raw("(SELECT patient_id, MAX(discharge_date) AS discharge_date FROM patient_discharge
                    WHERE discharge_date IS NULL OR discharge_date > '$startDate'
                    GROUP BY patient_id) as pd"), 'patients.id', '=', 'pd.patient_id')

    // Join master_services table
    ->join('master_services', 'master_ratecard.service_id', '=', 'master_services.id')

    // Subquery to get the latest schedule details for each patient
    ->leftJoinSub(
        DB::table('patient_schedules')
            ->select('patient_id', DB::raw('MAX(id) as latest_schedule_id')) // Get the latest schedule ID for each patient
            ->groupBy('patient_id'),
        'latest_ps',
        function ($join): void {
            $join->on('patients.id', '=', 'latest_ps.patient_id');
        }
    )

    // Join the latest patient_schedules details
    ->leftJoin('patient_schedules as ps2', 'latest_ps.latest_schedule_id', '=', 'ps2.id')
    ->leftJoin('master_beds', 'ps2.bed_id', '=', 'master_beds.id')
    ->leftJoin('master_rooms', 'master_beds.room_id', '=', 'master_rooms.id')
    ->leftJoin(DB::raw("(SELECT patient_id, id as latest_lead_id, care_plan, status FROM leads WHERE id IN (
        SELECT MAX(id) FROM leads GROUP BY patient_id
    )) as latest_lead"), function ($join): void {
    $join->on('patients.id', '=', 'latest_lead.patient_id');
})

    // Conditions to fetch the latest record if service_id is 89
    ->where('patients.branch_id', $currentBranchId)
    ->where('master_ratecard.service_id', 89)
    ->where(function ($query): void {
        $query->whereNull('master_ratecard.end_date') // End date is null (active)
            ->orWhere('master_ratecard.end_date', '>', DB::raw('ps2.schedule_date')); // Or end date is after the schedule date
    })
    ->where('master_ratecard.start_date', '<=', DB::raw('ps2.schedule_date')) // Start date should be before or equal to the schedule date

    // Select necessary fields
    ->select(
        DB::raw('CONCAT_WS(" ", patients.name_prefix, patients.first_name, patients.middle_name, patients.last_name) as full_name'),
        'master_services.service_name',
        DB::raw("GROUP_CONCAT(DISTINCT master_ratecard.rate ORDER BY master_ratecard.created_at SEPARATOR ',') as Rates"),
        DB::raw("GROUP_CONCAT(DISTINCT DATE_FORMAT(master_ratecard.start_date, '%d-%m-%Y') ORDER BY master_ratecard.created_at SEPARATOR ',') as Schedule_Dates"),
        'patients.patient_id',
        DB::raw('IF(pd.patient_id IS NOT NULL, "Discharged", "Ongoing") as status'), // Set status based on discharge record
        'master_rooms.room_number',
        DB::raw("'' as Service_IDs"),
        DB::raw("
       CASE
                           WHEN latest_lead.care_plan = 1 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 2 THEN 'Palliative Care'
                            WHEN latest_lead.care_plan = 3 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 4 THEN 'Transition Care'
                            WHEN latest_lead.care_plan = 5 THEN 'Mind and Memory Care'
                            WHEN latest_lead.care_plan = 6 THEN 'Rehab Care'
                            WHEN latest_lead.care_plan = 7 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 8 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 9 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 10 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 11 THEN 'Athulya Assisted Living'
                            WHEN latest_lead.care_plan = 12 THEN 'Athulya Assisted Living'
                            ELSE 'Unknown'
        END AS care_plan
    ")
    )
    ->groupBy('patients.id', 'master_services.service_name', 'patients.patient_id', 'pd.patient_id', 'master_rooms.room_number');
    }


    // Combine all queries
    $finalQuery = $queries[0];
    for ($i = 1; $i < count($queries); $i++) {
        $finalQuery->unionAll($queries[$i]);
    }

    $rates = $finalQuery->get();

    // Initialize totals
    $grandTotal = 0;
    $subtotals = [];

    // Convert Rates and Schedule_Dates to arrays, calculate number of days, and compute subtotals and grand total
    foreach ($rates as $rate) {
        $rate->Rates = explode(',', $rate->Rates);
        $rate->Schedule_Dates = explode(',', $rate->Schedule_Dates);

        // Sort Schedule_Dates
        usort($rate->Schedule_Dates, function ($a, $b) {
            $dateA = DateTime::createFromFormat('d-m-Y', $a);
            $dateB = DateTime::createFromFormat('d-m-Y', $b);
            return $dateA <=> $dateB;
        });

        // Count occurrences of each date
        $dateCounts = array_count_values($rate->Schedule_Dates);

        // Separate single and double session dates
        $singleSessionDates = [];
        $doubleSessionDates = [];
        foreach ($dateCounts as $date => $count) {
            if ($count == 1) {
                $singleSessionDates[] = $date;
            } elseif ($count > 1) {
                // $doubleSessionDates[] = $date;
                $doubleSessionDates[] = "{$date} ({$count})";
            }
        }

        // Store the single and double session dates
        $rate->SingleSessionDates = implode(', ', $singleSessionDates);
        $rate->DoubleSessionDates = implode(', ', $doubleSessionDates);

        // Calculate Two Session days by counting duplicate dates
        $rate->twoSessionDays = count($doubleSessionDates);
        $rate->Number_of_Days = count($singleSessionDates) ;
       // $rate->twoSessionDays = count($doubleSessionDates) ;
        // Calculate Number_of_Days and ensure it doesn't go negative
      //  $rate->Number_of_Days = max(count($rate->Schedule_Dates) - $rate->twoSessionDays * 2, 0);

        $rate->RateUnique = implode(', ', array_unique($rate->Rates));

        $gstRates = array_map(function($rate) {
            return number_format($rate * 1.18, 2, '.', ''); // Multiply by 1.18 and format to 2 decimal places
        }, array_unique($rate->Rates));

        $rate->GstRateUnique = implode(', ', $gstRates); // Join the formatted GST rates into a string


        // Calculate subtotal for each rate
        $subtotal = array_sum($rate->Rates);
        $subtotals[] = $subtotal;

        // Add to grand total
        $grandTotal += $subtotal;

        // Convert Schedule_Dates back to string for display
        $rate->Schedule_Dates = implode(', ', $rate->Schedule_Dates);
    }



    $services = Services::all();
    $patients = Patient::select(
        'patient_id',
        'name_prefix',
        'first_name',
        'middle_name',
        'last_name'
    )
    ->where('branch_id', $currentBranchId)
    ->whereNotNull('patient_id')
    ->get();

    foreach ($patients as $patient) {
        $patient->full_name = trim("{$patient->name_prefix} {$patient->first_name} {$patient->middle_name} {$patient->last_name}");
    }

    return view('reports/operations.residentservicesall', compact('services', 'patients', 'rates', 'subtotals', 'grandTotal'));
}

